triple U = lineintersectplan(B, E - B, R, cross(R - S, R - T));
triple V = lineintersectplan(D, E - D, R, cross(R - S, R - T));

draw(E--B--C--D--cycle);
draw(C--E);
draw(A--B,dashed);
draw(A--D,dashed);
draw(A--E,dashed);
draw(U--R--V,dashed);
draw(U--S);
draw(V--T);
draw(S--T,dashed);

