WW = (0,0);
G = (0,sqrt(2));
H = G + (0,2);
X = H + (0,1+2*sqrt(2));
A = X + (1+2*sqrt(2),0);
B = A + (1,0);
Y = B + (sqrt(2), 0);
C = Y + (0,-sqrt(2));
D = C - (0,3);
Z = D - (0,2*sqrt(2));
