label("$B$",B,S);
label("$C$",C,NW);
label("$D$",D,NW);
label("$E$",Ep,NE);
label("$F$",F,NE);
label("$G$",G,SE);
label("$H$",H,SW);
label("$J$",J,N);

label("$1$",(A+B)/2,N);
label("$7$",(B+C)/2,NE);
label("$2$",(C+D)/2,SE);
label("$4$",(D+Ep)/2,S);
[/asy]

