solution:
[asy]
pair P,Q,R;
P = (0,0);
Q = (3*sqrt(24),0);
R = (0,3);
draw(P--Q--R--P);
draw(rightanglemark(Q,P,R,18));
label("$P$",P,SW);
label("$Q$",Q,SE);
