draw((0,0,-1)--(0,0,-2));
draw((1,0,0)--(2,0,0));
draw((0,1,0)--(0,2,0));
draw((0,0,1)--(0,0,2));
draw(surface(sphere(1)),gray(0.8));

label("$\rho = c$", (1,1.2,-0.6));
