draw(surface(U--(V + U)--(U + V + W)--(W + U)--cycle),gray(0.9),nolight);
draw(surface(W--(V + W)--(U + V + W)--(W + U)--cycle),gray(0.5),nolight);
draw(O--(-3*I), dashed, Arrow3(6));
draw(O--3*J, Arrow3(6));
draw(O--3*K, Arrow3(6));
draw(U--(V + U)--(U + V + W)--(V + W)--W);
draw(U--(W + U)--(U + V + W));
draw((W + U)--W);
draw((V + U)--V--(V + W),dashed);
draw(O--V,dashed,Arrow3(6));
draw(O--W,Arrow3(6));
draw(O--U,Arrow3(6));

