  if (num1 > num2) return makeMultiplesOfDigit(num2, num1);
  let isEven = 1 - num1 % 2;
  if (num1 == num2) return isEven; // base case
  return isEven + makeMultiplesOfDigit(num1 + 1, num2);
}

