V = (P + reflect(B,C)*(P))/2;
W = (P + reflect(C,D)*(P))/2;
X = (P + reflect(D,E)*(P))/2;
Y = (P + reflect(E,F)*(P))/2;
Z = (P + reflect(F,A)*(P))/2;

draw(A--B--C--D--E--F--cycle);
draw(U--X,dotted);
draw(V--Y,dotted);
draw(W--Z,dotted);
draw(A--P);
draw(B--P);
