pair F, P, Q;

F = (1,0);

path parab = ((-4)^2/4,-4);

for (y = -4; y <= 4; y = y + 0.01) {
  parab = parab--(y^2/4,y);
}

P = intersectionpoint(F--(F + 5*(1,2)),parab);
Q = intersectionpoint(F--(F - 5*(1,2)),parab);

draw(parab,red);
draw((-2,0)--(4^2/4,0));
draw((0,-4)--(0,4));
draw((-1,-4)--(-1,4),dashed);
draw(P--Q);
draw(P--(-1,P.y));
draw(Q--(-1,Q.y));

